tinymce.PluginManager.add('image', function(editor, url) {

    editor.addButton('image', {
        icon: 'image',
        tooltip: 'Insert/edit image',
        stateSelector: 'img:not([data-mce-object])'
    });

    editor.addMenuItem('image', {
        icon: 'image',
        text: 'Insert image',
        context: 'insert',
        prependToContext: true
    });
});
